mapper.insert:
INSERT INTO rss_article (
    feed_id, article_guid, title, link, description,
    author, pub_date, category, enclosure_url, enclosure_type, image_url
) VALUES (
    #{feedId}, #{articleGuid}, #{title}, #{link}, #{description},
    #{author}, #{pubDate}, #{category}, #{enclosureUrl}, #{enclosureType}, #{imageUrl}
)

INSERT INTO rss_article (
    feed_id, article_guid, title, link, description,
    author, pub_date, category, enclosure_url, enclosure_type, image_url
) VALUES

    (
        #{item.feedId}, #{item.articleGuid}, #{item.title}, #{item.link}, #{item.description},
        #{item.author}, #{item.pubDate}, #{item.category}, #{item.enclosureUrl}, #{item.enclosureType}, #{item.imageUrl}
    )

mapper.select:
SELECT a.*, f.feed_name, f.feed_image_url
FROM rss_article a
LEFT JOIN rss_feed f ON a.feed_id = f.id
WHERE a.is_deleted = 0

    AND a.feed_id = #{feedId}


    AND a.is_read = #{isRead}


    AND a.is_favorite = #{isFavorite}


    AND (a.title LIKE CONCAT('%', #{keyword}, '%')
         OR a.description LIKE CONCAT('%', #{keyword}, '%'))

ORDER BY a.pub_date DESC, a.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM rss_article a
WHERE a.is_deleted = 0

    AND a.feed_id = #{feedId}


    AND a.is_read = #{isRead}


    AND a.is_favorite = #{isFavorite}


    AND (a.title LIKE CONCAT('%', #{keyword}, '%')
         OR a.description LIKE CONCAT('%', #{keyword}, '%'))

SELECT article_guid
FROM rss_article
WHERE feed_id = #{feedId}
  AND article_guid IN

    #{guid}

  AND is_deleted = 0

mapper.update:
UPDATE rss_article SET is_read = #{isRead}
WHERE id IN

    #{id}

AND is_deleted = 0
